import React from 'react';
import './index.css';
import Square from './square'

// To collect data from multiple children, or to have two child components communicate with each other, you need to declare the shared state in their parent component instead

export default class Board extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      squares: Array(9).fill(null),
      xIsNext : true,
    }
  }

  calculateWinner(squares) {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];

    for (let i = 0; i < lines.length; ++i) {
      const [a, b, c] = lines[i];
      if (squares[a] != null 
          && (squares[a] === squares[b])
          && (squares[b] === squares[c])) {
        return squares[a];
      }
    }

    return null;
  }

  handleClick(i) {
    // create a copy of origin squares
    // immutability helps react to determine when to re-render
    const squares = this.state.squares.slice();

    if (this.calculateWinner(squares) || squares[i]) {
      return;
    }


    squares[i] = this.state.xIsNext ? 'X' : 'O';

    // trigger re-rendering
    this.setState({
      squares: squares,
      xIsNext: !this.state.xIsNext,
    }) 
  }

  renderSquare(i) {
    return (
      // pass props
      // the Square components are now controlled components
      // the Board has full control over it
      <Square
        value={this.state.squares[i]}
        onClick={() => this.handleClick(i)}
      />
    )
  }

  render() {
    const winner = this.calculateWinner(this.state.squares);

    let status;
    if (winner != null) {
      status = `Winner: ${winner}`
    } else {
      status = `Next player: ${this.state.xIsNext ? 'X' : 'O'}`;
    }

    /*
      following code will be converted to

      return React.createElement('div', {className: 'status'},
        React.createElement('div', ...),
        ...
      );
    */
    return (
      <div>
        <div classNamae='status'>{status}</div>
        <div className='board-row'>
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className='board-row'>
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className='board-row'>
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    )
  }

}
