import React from 'react';
import './index.css';

export default class Square extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: null,
    };
  }

  render() {
    return (
      // use props
      // change state on click
      // changing state will re-render the Square
      <button className='square' onClick={() => this.setState({ value: 'X' })}>
        {this.state.value}
      </button>
    )
  }
}
